INSERT INTO `goods` (`id`, `catalog_id`, `title`, `description`, `price`, `unit`, `image`, `meta_description`, `meta_keywords`) VALUES
(1, 0, 'Курс управления аэрокосмической техникой', 'Годовой курс обучения управлению аэрокосмической техникой, обслуживанию оборудования и навигации', 540000, 'руб/год', './img/Raptor_Engine_Unofficial_Combustion_Scheme.svg', 'Годовой курс обучения управлению аэрокосмической техникой, обслуживанию оборудования и навигации', 'Курс навигации, аэрокосмический курс'),
(2, 1, 'Перхлорат аммония', 'Окислитель для ракетного топлива', 5600, 'руб/кг', '', 'Окислитель для ракетного топлива перхлорат аммония', 'перхлорат аммония купить, твердое топливо'),
(3, 7, 'SS-520 - орбитальная версия', 'Высота ракеты — 9,54 м, стартовая масса — 2,6 т. Может вывести на НОО полезную нагрузку весом более 4 кг. Тяга двигателя первой ступени составляет 14,6 тонн (145—185 кН), удельный импульс — 265 с. Масса топлива первой ступени — 1587 кг, второй — 325, третьей — 78. Ориентацию ракеты после отделения первой ступени обеспечивают система ラムライン (Ramurain) — четыре импульсных двигателя, работающих на сжатом азоте. Азот хранится в баке объёмом 5,7 л при давлении 230 бар. Система управления и передачи телеметрии создана компанией Canon Electronics. Третья ступень не имела телеметрической системы. Для определения окончательных параметров орбиты на ней был установлен GPS-датчик, передававший сигнал через систему Иридиум.<br><br>\r\n\r\nОдной из особенностей ракеты-носителя является широкое использование не специализированных, а доступных потребительских компонентов. Это сделано для снижения стоимости ракеты-носителя, что влияет на стоимость запуска полезной нагрузки.<br><br>\r\n\r\nПервый пуск был профинансирован Министерством экономики, торговли и промышленности; стоимость запуска около 400 млн иен (3,5 млн USD)<br><br>\r\n\r\nНа момент запусков это была самая маленькая ракета-носитель для запуска полезной нагрузки на околоземную орбиту.', 62350000, 'руб', './img/ss-520-4-1484461680.jpg', 'Японская твердотопливная ракета, изготавливается компанией IHI Aerospace', 'SS-520 для орбитальных запусков, твердотопливный ускоритель'),
(4, 8, 'SS-520 - геофизическая версия', 'Геофизическая двухступенчатая версия ракеты запускалась Институтом космических исследований и астронавтики Японии (англ. Institute of Space and Astronautical Science, ISAS) для проведения научных исследований в магнитосфере и в условиях микрогравитации. Ракета изготавливается компанией IHI Aerospace.  Позволяет выполнять суборбитальные запуски с нагрузкой до 140 кг на высоту до 800 км. Высота ракеты составляет 9,65 м, диаметр — 0,52 м, масса — 2,6 т. Японские высотные исследовательские ракеты Первая ступень сделана на основе твердотопливной одноступенчатой метеорологической ракеты S-520. Корпус двигателя выполнен из высокопрочной стали HT-140. Вертикальные стабилизаторы, расположенные в нижней части первой ступени, обеспечивают управление ракетой во время полёта. Вторая ступень выполнена полностью из углепластикового композиционного материала. Обе ступени используют твёрдое ракетное топливо на основе HTPB. Головной обтекатель выполнен из стеклотекстолита. Стабилизация ракеты в полёте осуществляется закруткой вдоль продольной оси с помощью стабилизаторов. Стабилизаторы выполнены в виде трёхслойного сендвича из алюминиевых сот покрытых обшивкой из угле- и стекло-пластика. Передняя кромка стабилизаторов изготовлена из титана', 32500000, 'руб', './img/otpravili-na-orbitu-ss-520-5.jpg', 'Геофизическая версия SS-520, создана путём добавления третьей ступени к высотной исследовательской ракете SS-520 и соответствующей доработки бортовых систем. В третьей ступени так же используются твёрдое ракетное топливо на основе HTPB.', 'SS-520 для метеоисследований, метеорологическая ракета, твердотопливный ускоритель'),
(5, 7, 'S-520', 'S-520 был разработан для замены зондирующих ракет типа K-9M и K-10. Это активная одноступенчатая зондирующая ракета, способная запускать груз весом 100 кг свыше 300 км и обеспечивающая более пяти минут полета в условиях микрогравитации для экспериментов. Первый запуск состоялся в 1980 году, а последний полет состоялся 11 сентября 2015 года.<br><br>\r\n\r\nМасса брутто: 2300 кг (5000 фунтов)<br>\r\nМасса без топлива: 400 кг (880 фунтов)<br>\r\nВремя горения: 29 с<br>\r\nВысота: 9,00 м (29,50 фута)<br>\r\nДиаметр: 0,52 м (1,70 фута)<br>\r\nАпогей: 430 км', 40000000, 'руб', './img/unnamed.jpg', 'S-520, одноступенчатая зондирующая ракета, способная запускать груз весом 100 кг свыше 300 км', 'S-520, зондирующая ракета, твердотопливный ускоритель'),
(6, 7, '«Старт»', 'Твердотопливная космическая ракета-носитель (РН), сконструированная в НТЦ «Комплекс-МИТ» на базе межконтинентальной баллистической ракеты 15Ж58 подвижного грунтового ракетного комплекса «Тополь».<br><br>\r\n\r\nПредназначена для запуска малогабаритных космических аппаратов на низкие околоземные орбиты, в том числе по коммерческим заказам.<br><br>\r\n\r\nМодификация -  пятиступенчатая, причём вторая ступень ракеты, аналогичная второй ступени 15Ж58, встраивается дважды. Полезная нагрузка для низких орбит (400 км) составляет примерно 850 кг для ракеты «Старт». Максимальный диаметр обоих типов ракет составляет 1,8 метра. Высота ракеты составляет 28,8 м, масса — 60 т.', 85000000, 'руб', './img/St-1-1w.jpg', 'твердотопливная космическая ракета-носитель (РН), сконструированная в НТЦ «Комплекс-МИТ» на базе межконтинентальной баллистической ракеты 15Ж58 подвижного грунтового ракетного комплекса «Тополь»', 'Ракета «Старт», твердотопливный ускоритель, твердотопливный носитель пятиступенчатый, продукция «Комплекс-МИТ»'),
(7, 7, '«Старт-1»', 'Твердотопливная космическая ракета-носитель (РН), сконструированная в НТЦ «Комплекс-МИТ» на базе межконтинентальной баллистической ракеты 15Ж58 подвижного грунтового ракетного комплекса «Тополь».<br><br>\r\n\r\nПредназначена для запуска малогабаритных космических аппаратов на низкие околоземные орбиты, в том числе по коммерческим заказам.<br><br>\r\n\r\nЧетырёхступенчатая модификация. Полезная нагрузка для низких орбит (400 км) составляет примерно 500 кг. Высота — 22,7 м, масса — 47 т.', 55000000, 'руб', './img/1473771069_start-1.jpg', 'твердотопливная космическая ракета-носитель (РН), сконструированная в НТЦ «Комплекс-МИТ» на базе межконтинентальной баллистической ракеты 15Ж58 подвижного грунтового ракетного комплекса «Тополь», четырёхступенчатая модификация', 'Ракета «Старт», твердотопливный ускоритель, твердотопливный носитель четырёхступенчатый, продукция «Комплекс-МИТ»'),
(8, 7, 'Боковой ускоритель МТКК Спейс шаттл', 'Твердотопливный ракетный ускоритель, пара которых обеспечивают 83 % стартовой тяги МТКК «Спейс шаттл». Это крупнейший и наиболее мощный твердотопливный ракетный двигатель из когда-либо летавших, самая большая ракета из созданных для повторного использования и также наиболее мощный ракетный двигатель из всех применявшихся, на твёрдом или жидком топливе, в истории.<br><br>\r\n\r\nДва боковых ускорителя дают основную тягу для отрыва системы «Спейс шаттл» от стартового стола и подъёма до высоты около 46 километров. Кроме этого, оба SRB несут на себе весь вес внешнего бака и орбитера, передавая нагрузки через свои конструкции на мобильную пусковую платформу.<br><br>\r\n\r\nКаждый ускоритель имеет длину 45,5 метров, диаметр 3,7 метров и стартовую массу 580 000 кг, из которых около 499 000 кг составляет твёрдое топливо, а остальное приходится на конструкции ускорителя. Общая масса ускорителей составляет 60 % всей массы космической системы.<br><br>\r\n\r\nКаждый ускоритель имеет стартовую тягу (на уровне моря) приблизительно 12,45 МН (что в 1,8 раза больше, чем тяга двигателя F-1 использовавшегося в ракете Сатурн-5 для полётов на Луну и в 1,5 раза больше, чем самый мощный из когда-либо созданных ракетных двигателей на жидких компонентах топлива — РД-170), через 20 секунд после старта тяга вырастает до 13,8 МН (1400 тс). Их зажигание происходит только после получения подтверждения о запуске и нормальной работе трёх основных двигателей корабля, поскольку остановка твердотопливного ускорителя после его запуска невозможна. Через 75 секунд после отделения от системы на высоте 45 км, SRB, продолжая полет по инерции, достигают максимальной высоты полёта (приблизительно 67 километров), после чего, с помощью парашютной системы совершают посадку в океане, на расстоянии около 226 км от места старта. Ускорители приводняются в вертикальном положении, вертикальная скорость посадки составляет 23 м/с. На месте приводнения ускорители подбираются кораблями технической службы и доставляются на завод-изготовитель для восстановления и повторного использования.', 125000000, 'руб', './img/Freedom_Star_with_SRB.jpg', 'Твердотопливный ракетный ускоритель, пара которых обеспечивают 83 % стартовой тяги МТКК «Спейс шаттл». Это крупнейший и наиболее мощный твердотопливный ракетный двигатель из когда-либо летавших, самая большая ракета из созданных для повторного использования и также наиболее мощный ракетный двигатель из всех применявшихся, на твёрдом или жидком топливе, в истории.', 'боковой ускоритель, твердотопливный ускоритель, многоразовые космические корабли'),
(9, 7, 'SS-520-5', 'Орбитальная ракета-носитель, запущенная 3 февраля 2018 года.<br><br>\r\nТехнические характеристики<br>\r\nВысота - 31 фут (9,54 метра)<br>\r\nВес - 2,9 тонны (2,6 метрической тонны)<br>\r\nДиаметр - 20 дюймов (52 сантиметра)<br>\r\nПолезная нагрузка на околоземную орбиту - ~ 9 фунтов (4 кг)', 75000000, 'руб', './img/unnamed.jpg', 'Орбитальная ракета-носитель, запущенная 3 февраля 2018 года.', 'твердотопливный носитель, S-серия, SS-520'),
(10, 7, 'Наро-1', 'Одноразовая двухступенчатая южнокорейская ракета-носитель, созданная при тесном сотрудничестве с российскими специалистами. По проекту, способна выводить более 100 кг полезной нагрузки на высоту до 300 км с наклонением 38&deg;.', 80000000, 'руб', './img/naro-1.jpg', 'Одноразовая двухступенчатая южнокорейская ракета-носитель, созданная при тесном сотрудничестве с российскими специалистами. По проекту, способна выводить более 100 кг полезной нагрузки на высоту до 300 км с наклонением 38&deg;.', 'Наро-1, твердотопливный ускоритель'),
(11, 7, 'Castor 30', 'Двигатель CASTOR 30 основан на двигателе CASTOR 120, который использовался на ракетах-носителях Taurus I, Athena I и Athena II. Первый запуск нового двигателя состоялся в апреле 2013 года в качестве второй ступени ракеты средней грузоподъемности Ortaital Sciences Antares для полетов на Международную космическую станцию.\r\nВысота верхней ступени CASTOR 30 составляет 138 дюймов (3,5 м) в длину и 92 дюйма (2,3 м) в диаметре, а ее вес составляет 30 000 фунтов (14 000 кг). Двигатель номинально спроектирован как верхняя ступень, которая может функционировать также как вторая или третья ступень, в зависимости от конфигурации автомобиля.', 65000000, 'руб', '', 'Двигатель CASTOR 30 основан на двигателе CASTOR 120, который использовался на ракетах-носителях Taurus I, Athena I и Athena II. Первый запуск нового двигателя состоялся в апреле 2013 года в качестве второй ступени ракеты средней грузоподъемности Ortaital Sciences Antares для полетов на Международную космическую станцию.', 'твердотопливный двигатель, CASTOR 30, серия CASTOR'),
(12, 7, 'Castor 30XL', 'Двигатель CASTOR 30 основан на двигателе CASTOR 120, который использовался на ракетах-носителях Taurus I, Athena I и Athena II. Первый запуск нового двигателя состоялся в апреле 2013 года в качестве второй ступени ракеты средней грузоподъемности Ortaital Sciences Antares для полетов на Международную космическую станцию.\r\nВысота верхней ступени CASTOR 30 составляет 138 дюймов (3,5 м) в длину и 92 дюйма (2,3 м) в диаметре, а ее вес составляет 30 000 фунтов (14 000 кг). Двигатель номинально спроектирован как верхняя ступень, которая может функционировать также как вторая или третья ступень, в зависимости от конфигурации автомобиля.\r\nТвердотопливный ракетный двигатель CASTOR 30XL имеет длину 236 дюймов (6,0 м) и 92 дюйма (2,3 м) в диаметре, а его вес составляет приблизительно 56 000 фунтов (25 000 кг). Сопло имеет длину восемь футов с погруженной конструкцией с высокой степенью расширения (56: 1) и выходным конусом двойной плотности.', 75000000, 'руб', '', 'Твердотопливный ракетный двигатель CASTOR 30XL имеет длину 236 дюймов (6,0 м) и 92 дюйма (2,3 м) в диаметре, а его вес составляет приблизительно 56 000 фунтов (25 000 кг). Сопло имеет длину восемь футов с погруженной конструкцией с высокой степенью расширения (56: 1) и выходным конусом двойной плотности.', 'твердотопливный двигатель, CASTOR 30XL, серия CASTOR'),
(13, 7, 'Castor 120', 'Castor 120 является производной от двигателя первой ступени ракеты MX («Миротворец»). «120» относится к запланированному весу в тысячах фунтов ускорителя в начале проекта. Однако сам продукт оказался легче, чем ожидалось. Сначала он использовался в качестве двигателя первой ступени Афины I Локхида Мартина, затем - первой и второй ступеней Афины II. После пробного запуска в августе 1995 года, первый запуск полезной нагрузки клиента произошел 22 августа 1997 года, когда Афина использовалась для запуска спутника НАСА Льюис. В 2006 году корпорация орбитальных наук согласились заплатить 17,5 млн. долл. США за двигатели Castor 120, используемые в ракетах-носителях Taurus XL для орбитальной углеродной обсерватории и спутников Glory. Основные твердотопливные ракетные ускорители (SRB-A) японской ракеты-носителя H-IIA основаны на Castor 120 и были совместно разработаны ATK и IHI Aerospace.', 77000000, 'руб', './img/Castor_120_for_OCO.jpg', 'Castor 120 является производной от двигателя первой ступени ракеты MX («Миротворец»)', 'твердотопливный двигатель, CASTOR 120, серия CASTOR'),
(14, 8, 'ММР-06', 'Длина 3,48 м, полный вес 130 кг. ММР-06 изготавливалась в двух модификациях: головная часть первой была конической; у второй, ММР-06М, головная часть отделялась от ракеты и некоторое время продолжала подъём самостоятельно, её называли «дротик». Максимальная высота подъёма — 60-80 километров.<br><br>\r\nТехнические характеристики<br>\r\nПолная масса	134 ± 5 кг<br>\r\nМасса пустой ракеты	58 кг<br>\r\nМасса топлива	78 кг<br>\r\nДиаметр	0,2 м<br>\r\nДлина (полная)	4,14 м<br>\r\nРасстояние от дюз до центра<br> тяжести при пуске	1,445 м<br>\r\nРасстояние от дюз до центра тяжести на момент выгорания топлива	1,872 м', 20000000, 'руб', '', 'ММР-06 — советская метеорологическая ракета. Длина 3,48 м, полный вес 130 кг. ММР-06 изготавливалась в двух модификациях: головная часть первой была конической; у второй, ММР-06М, головная часть отделялась от ракеты и некоторое время продолжала подъём самостоятельно, её называли «дротик». Максимальная высота подъёма — 60-80 километров.', 'метеорологическая ракета, твердотопливный ускоритель'),
(15, 1, 'HTPB', 'Полибутадиен с концевыми гидроксильными группами (HTPB) применяется в твердотопливных ракетных двигателях в качестве связующего вещества между топливом и окислителем, а также в качестве топлива для гибридных ракетных двигателей', 12000, 'руб/л', '', 'Полибутадиен с концевыми гидроксильными группами (HTPB) применяется в твердотопливных ракетных двигателях в качестве связующего вещества между топливом и окислителем, а также в качестве топлива для гибридных ракетных двигателей', 'HTPB, полибутадиен с концевыми гидроксильными группами, связующее вещество для ракетного топлива, топливо для гибридных ракетных двигателей'),
(16, 7, 'Вега', 'Лёгкая одноразовая ракета-носитель, совместно разрабатываемая с 1998 года Европейским космическим агентством (ESA) и Итальянским космическим агентством (ASI).\r\nПолезная нагрузка РН «Вега» составляет 1500 кг на полярную орбиту высотой ~700 км. РН разработана для вывода полезной нагрузки на низкую опорную орбиту и солнечно-синхронную орбиту. В первом полёте РН лёгкого класса вывела основную полезную нагрузку — спутник «LARES» весом 400 кг на высоту 1450 км с наклоном орбиты 71,5o. В отличие от большинства одноклассных РН, «Вега» способна выводить сразу несколько космических аппаратов. Основные типы аппаратов, являющиеся потенциальной загрузкой:<br><br>\r\n\r\nМикроспутники — до 300 кг;<br>\r\nМиниспутники — от 300 до 1 000 кг;<br>\r\nМалые спутники — от 1 000 до 2 000 кг.', 120000000, 'руб', './img/vega.svg', 'Лёгкая одноразовая ракета-носитель, совместно разрабатываемая с 1998 года Европейским космическим агентством (ESA) и Итальянским космическим агентством (ASI).', 'Вега, ракета-носитель, твердотопливный ускоритель'),
(17, 7, 'Р80', 'Первая ступень носителя \"Вега\" имеет длину 10,5 м, диаметр 3 м, масса топлива — 88 т, двигатель РДТТ, тяга 3040 кН, коэффициент расширения сопла 16, Время работы 107 с. Изготовлена из углепластика с эпоксидной основой, сопло двигателя оснащено электроприводом отклонения. 30 ноября 2006 года было успешно завершено первое испытание. 4 декабря 2007 года успешно прошло второе испытание, в результате которого была достигнута тяга 190 тс при длительности работы 111 с, параметры работы двигателя находились в пределах заявленных', 30000000, 'руб', '', 'Первая ступень РН \"Вега\" имеет длину 10,5 м, диаметр 3 м, масса топлива — 88 т, двигатель РДТТ, тяга 3040 кН, коэффициент расширения сопла 16, Время работы 107 с. Изготовлена из углепластика с эпоксидной основой, сопло двигателя оснащено электроприводом отклонения.', 'Р80, ракетная ступень твердотопливная, РДТТ двигатели, ступень ракеты \"Вега\"'),
(18, 7, 'Zefiro 23', 'Развитие двигателя Zefiro было инициировано компанией «Avio» и профинансировано как «Avio», так и ISA. Является второй ступенью РН «Вега». Изготовлена из углепластика с эпоксидной основой, сопло — из углеродного волокна с фенольной связкой, а вставка в критическом сечении сопла — из углерод-углеродного материала. Использование этих материалов обусловило как уменьшение веса конструкции, так и увеличение её прочности. Длина — 7,5 м, диаметр — 1,9 м, масса топлива — 23,9 т, тяга — 1 200 кН, коэффициент расширения сопла — 25, время работы 71,6 с. Первый успешный запуск был осуществлен 26 июня 2006 года в Сальто-ди-Квиро, Сардиния, Италия. Второй запуск 27 марта 2008 года был успешно завершен присвоением квалификации ступени ракеты-носителя.', 30000000, 'руб', '', 'Развитие двигателя Zefiro было инициировано компанией «Avio» и профинансировано как «Avio», так и ISA. Является второй ступенью РН «Вега». Изготовлена из углепластика с эпоксидной основой, сопло — из углеродного волокна с фенольной связкой, а вставка в критическом сечении сопла — из углерод-углеродного материала.', 'Zefiro 23, ракетная ступень твердотопливная, РДТТ двигатели, ступень ракеты \"Вега\"'),
(19, 7, 'Zefiro 9', 'Третья ступень РН Вега, имеет длину — 3,85 м, диаметр — 1,9 м, масса топлива — 10,1 т, тяга — 213 кН, коэффициент расширения сопла — 56, время работы 117 с. Первые испытания успешно были проведены 20 декабря 2005 года на полигоне Сальто-ди-Квиро, на юго-восточном побережье Сардинии, Италия. Второе испытание состоялось 28 марта 2007 года в Сальто-ди-Квиро. Однако на 35-й секунде работы двигателя произошло резкое падение внутреннего давления, приведшее к потере тяги. Это было вызвано конструкционными недостатками. 23 октября 2008 года были проведены успешные повторные испытания с модифицированным соплом, зарегистрированном как Zefiro-9A. 28 апреля 2009 года на полигоне Сальто-ди-Квиро были проведены окончательные огневые испытания с присвоением квалификации ступени РН Вега', 35000000, 'руб', '', 'Третья ступень РН Вега, имеет длину — 3,85 м, диаметр — 1,9 м, масса топлива — 10,1 т, тяга — 213 кН, коэффициент расширения сопла — 56, время работы 117 с.', 'Zefiro 9, ракетная ступень твердотопливная, РДТТ двигатели, ступень ракеты \"Вега\"'),
(20, 8, 'Black Brant', 'Канадская метеорологическая ракета.<br><br>\r\n\r\nРазработана компанией Bristol Aerospace. Первый запуск был осуществлён в сентябре 1959 года.<br><br>\r\n\r\nРакета Black Brant способна поднять полезную нагрузку массой 68 кг на высоту 150 км. Эти ракеты использовались неоднократно Канадским космическим агентством и НАСА.<br><br>\r\n\r\nВсего на 2010 год было построено около 800 ракет Black Brant различных модификаций.<br><br>\r\n\r\nBlack Brant I<br>\r\nНагрузка: 68 кг<br>\r\nМаксимальная высота полёта: 225 км<br>\r\nТяга: 111 кН<br>\r\nСтартовая масса: 730 кг<br>\r\nДиаметр: 0.26 м<br>\r\nДлина: 7.41 м<br>', 28000000, 'руб', './img/Black_Brant.jpg', 'Метрологическая ракета Black Brant способна поднять полезную нагрузку массой 68 кг на высоту 150 км. Эти ракеты использовались неоднократно Канадским космическим агентством и НАСА.', 'Метрологическая ракета, Black Brant I, твердотопливный ускоритель'),
(21, 2, 'AVUM', 'AVUM (англ. Attitude Vernier Upper Module) — четвёртая ступень РН «Вега». Длина — 1,74 м, диаметр — 1,9 м, масса топлива — 550 кг, тяга — 2,45 кН, Время работы — 315,2 с. Оборудована некриогенным ЖРД РД-843 многократного включения с вытеснительной системой подачи. Горючее — несимметричный диметилгидразин, Окислитель — азотный тетраоксид. Ступень оборудована двигателем и авионикой. Содержит основные компоненты авионики РН. Маршевый двигатель РД-843 был разработан украинским КБ «Южное» и изготовлен на ПО «Южмаш»', 30000000, 'руб', '', 'AVUM (англ. Attitude Vernier Upper Module) — четвёртая ступень РН «Вега». Длина — 1,74 м, диаметр — 1,9 м, масса топлива — 550 кг, тяга — 2,45 кН, Время работы — 315,2 с.', 'AVUM, жидкотопливный ускоритель, ступень ракеты \"Вега\", двигатель на диметилгидразине'),
(22, 2, 'РД-843', 'РД-843 — украинский жидкостный ракетный двигатель, который работает на топливной паре НДМГ и АТ. Двигатель рассчитан на 5 запусков в полёте и может совершать отклонения до 10 градусов в каждом направлении. Масса двигателя составляет 16,5 кг, тяга — 0,25 тс.\r\n\r\nДвигатель был разработан КБ «Южное» для компании Fiat Avio и производится на заводе «Южмаш». Он использует камеру сгорания от двигателя РД-869 советской МБР Р-36М, рассчитан на эксплуатацию с вытеснительной системой подачи топлива. РД-843 прошёл испытания в 74 тестах, 140 зажиганиях, достигнув в целом наработку в 8201 с, что приблизительно равно 12 срокам службы на 4 двигателях.\r\n\r\nСейчас используется как основной двигатель 4-й ступени ракеты-носителя «Вега». По состоянию на ноябрь 2017 года двигатель был успешно использован на 11 орбитальных запусках.', 25000000, 'руб', '', 'РД-843 — украинский жидкостный ракетный двигатель, который работает на топливной паре НДМГ и АТ. Двигатель рассчитан на 5 запусков в полёте и может совершать отклонения до 10 градусов в каждом направлении. Масса двигателя составляет 16,5 кг, тяга — 0,25 тс.', 'РД-843, жидкостный ракетный двигатель, двигатель на диметилгидразине'),
(23, 2, 'RS-56', 'RS-56 - американский ракетный двигатель на жидком топливе, разработанный Rocketdyne. Присутствуют два варианта этого двигателя, оба для использования на серии ракет Atlas II. Первый, RS-56-OBA, играет роль ускорителя, в то время как RS-56-OSA разработан для использования в качестве маршевого и производит меньшую тягу, но с более высоким удельным импульсом.', 45000000, 'руб', './img/rs56.jpg', 'RS-56 - американский ракетный двигатель на жидком топливе, разработанный Rocketdyne. Присутствуют два варианта этого двигателя, оба для использования на серии ракет Atlas II.', 'RS-56, жидкотопливный двигатель, Rocketdyne'),
(24, 2, 'Prometheus', 'Ракетный двигатель Prometheus стал результатом усилий Европейского космического агентства в создании метанового ускорителя многоразового использования, потенциально для ракеты Ariane 6 или его преемника.\r\nНекоторые детали выполнены с помощью 3D-печати.<br><br>\r\nИспользуется принцип ЖРД открытого цикла<br><br>\r\nТяга - 100 тонн.<br>\r\nДавление в камере сгорания<br> приблизительно 100 бар (10000 кПа).<br>\r\nДвигатель многоразовый, может использоваться от 3 до 5 раз.', 43000000, 'руб', '', 'Ракетный двигатель Prometheus стал результатом усилий Европейского космического агентства в создании метанового ускорителя многоразового использования, потенциально для ракеты Ariane 6 или его преемника.', 'Prometheus, жидкостный двигатель, метановый двигатель, многоразовый двигатель, Европейское космическое агенство'),
(25, 2, 'SpaceX Raptor', 'Raptor («Раптор») — жидкостный ракетный двигатель, разрабатываемый компанией SpaceX. Двигатель закрытого цикла с полной газификацией компонентов топлива, работающий на жидких метане и кислороде, планируется применять на ракета-носителе SuperHeavy и космическом корабле Starship.', 60000000, 'руб', './img/Raptor-test-9-25-2016.jpg', 'Raptor («Раптор») — жидкостный ракетный двигатель, разрабатываемый компанией SpaceX. Двигатель закрытого цикла с полной газификацией компонентов топлива, работающий на жидких метане и кислороде, планируется применять на ракета-носителе SuperHeavy и космическом корабле Starship.', 'SpaceX Raptor, метановый двигатель, жидкостный двигатель'),
